SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select distinct task.task_id as taskId, task.cus_id as cusId, task.task_type as taskType, task.task_create_date as taskCreateDate, task.task_request_time as taskRequestTime, task.qnt as qnt, task.loan_totl_amt as LoanTotlAmt, task.loan_balance as loanBalance, task.task_huser as taskHuser, task.task_horg as taskHorg, task.manager_id as managerId, task.manager_br_id as managerBrId, task.check_time as checkTime, task.check_addr as checkAddr, task.agreed_person as agreedPerson, task.remarks as remarks, task.approve_status as approveStatus, task.industry_outlook as industryOutlook, task.repayment as repayment, task.reciprocal_type as reciprocalType, task.contact_information as contactInformation, ecif.client_name as chineseName, ecif.global_id as cardId, task.REPAYMENT_OTHER as repaymentOther from psp_check_task task left join qz_iesb_for_ecif ecif on task.cus_id = ecif.client_no where 1 = 1 and ecif.USER_ID = #{userId} and task.approve_status='0'
AND ecif.client_name=#{chineseName}
AND ecif.global_id=#{cardId}
AND ecif.client_no=#{clientNo}
AND task.task_type=#{checkType}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select count(*) from( select distinct task.task_id as taskId, task.cus_id as cusId, task.task_type as taskType, task.task_create_date as taskCreateDate, task.task_request_time as taskRequestTime, task.qnt as qnt, task.loan_totl_amt as LoanTotlAmt, task.loan_balance as loanBalance, task.task_huser as taskHuser, task.task_horg as taskHorg, task.manager_id as managerId, task.manager_br_id as managerBrId, task.check_time as checkTime, task.check_addr as checkAddr, task.agreed_person as agreedPerson, task.remarks as remarks, task.approve_status as approveStatus, task.industry_outlook as industryOutlook, task.repayment as repayment, task.reciprocal_type as reciprocalType, task.contact_information as contactInformation, ecif.client_name as chineseName, ecif.global_id as cardId, task.REPAYMENT_OTHER as repaymentOther from psp_check_task task left join qz_iesb_for_ecif ecif on task.cus_id = ecif.client_no where 1 = 1 and ecif.USER_ID = #{userId} and task.approve_status='0'
AND ecif.client_name=#{chineseName}
AND ecif.global_id=#{cardId}
AND ecif.client_no=#{clientNo}
AND task.task_type=#{checkType} ) a

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select distinct task.task_id as taskId, task.cus_id as cusId, task.task_type as taskType, task.task_create_date as taskCreateDate, task.task_request_time as taskRequestTime, task.qnt as qnt, task.loan_totl_amt as LoanTotlAmt, task.loan_balance as loanBalance, task.task_huser as taskHuser, task.task_horg as taskHorg, task.manager_id as managerId, task.manager_br_id as managerBrId, task.check_time as checkTime, task.check_addr as checkAddr, task.agreed_person as agreedPeson, task.remarks as remarks, task.approve_status as approveStatus, task.industry_outlook as industryOutlook, task.repayment as repayment, task.reciprocal_type as reciprocalType, task.contact_information as contactInformation, ecif.client_name as chineseName, ecif.global_id as cardId, task.REPAYMENT_OTHER as repaymentOther from psp_check_task task left join qz_iesb_for_ecif ecif on task.cus_id = ecif.client_no where 1 = 1 and task.approve_status='1'
AND ecif.client_name=#{chineseName}
AND ecif.global_id=#{cardId}
AND ecif.client_no=#{clientNo}
AND task.task_type=#{checkType}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

			select 
				count(*)
  			from psp_check_task task
  			left join qz_iesb_for_ecif ecif on task.cus_id = ecif.client_no
 			where 1 = 1
   			and task.approve_status='1'
   			 
				AND ecif.client_name=#{chineseName}
			
			
				AND ecif.global_id=#{cardId}
			
			
				AND ecif.client_no=#{clientNo}
			
			
				AND task.task_type=#{checkType}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select distinct task.task_id as taskId, task.cus_id as cusId, task.task_type as taskType, task.task_create_date as taskCreateDate, task.task_request_time as taskRequestTime, task.qnt as qnt, task.loan_totl_amt as LoanTotlAmt, task.loan_balance as loanBalance, task.task_huser as taskHuser, task.task_horg as taskHorg, task.manager_id as managerId, task.manager_br_id as managerBrId, task.check_time as checkTime, task.check_addr as checkAddr, task.agreed_person as agreedPeson, task.remarks as remarks, task.approve_status as approveStatus, task.industry_outlook as industryOutlook, task.repayment as repayment, task.reciprocal_type as reciprocalType, task.contact_information as contactInformation, ecif.client_name as chineseName, ecif.global_id as cardId, task.REPAYMENT_OTHER as repaymentOther from psp_check_task task left join qz_iesb_for_ecif ecif on task.cus_id = ecif.client_no inner join sys_user s on s.id=ecif.user_id where 1 = 1 and task.approve_status='0' and trunc(sysdate-to_date(task.TASK_CREATE_DATE,'yyyy-mm-dd')) > #{remindate}
AND ecif.client_name=#{chineseName}
AND ecif.global_id=#{cardId}
AND ecif.a_client_no=#{clientNo}
AND s.login=#{userNo}
AND task.task_type=#{checkType}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

			select 
				count(*)
  			from psp_check_task task
  			left join qz_iesb_for_ecif ecif on task.cus_id = ecif.client_no
  			inner join sys_user s on s.id=ecif.user_id
 			where 1 = 1
   			and task.approve_status='0'
   			and trunc(sysdate-to_date(task.TASK_CREATE_DATE,'yyyy-mm-dd')) >    #{remindate}
   			 
				AND ecif.client_name=#{chineseName}
			
			
				AND ecif.global_id=#{cardId}
			
			
				AND ecif.a_client_no=#{clientNo}
			
			
				AND task.task_type=#{checkType}
			
			
				AND s.login=#{userNo}
